package com.example.decodeimageview;

import androidx.appcompat.app.AppCompatActivity;

import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.os.Bundle;
import android.util.Base64;
import android.view.View;
import android.widget.ImageView;
import android.widget.Toast;

import com.mysql.jdbc.Blob;

import java.io.ByteArrayOutputStream;
import java.io.InputStream;
import java.sql.SQLException;

public class MainActivity extends AppCompatActivity {

    private String base_64_format= "";
    private ImageView imageView;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        imageView = findViewById(R.id.imageView);
        /*Code that sets the image*/
//        imageView.setImageBitmap(decodeBase64String(base_64_format));

        Task task = new Task(Task.GET_IMG_BLOB, new AsyncResponse() {
            @Override
            public void onFinish(Object output) {
                if(output!=null){
                    Toast.makeText(MainActivity.this, "img data successfully retrieved!", Toast.LENGTH_SHORT).show();
                    /*Code that sets the image*/
                    imageView.setImageBitmap(decodeBlobType((byte[]) output));
                }
            }
        });
        task.execute();
    }

    public Bitmap decodeBase64String(String img_string_data){
        //Decode base64 string
        byte[] bytes = Base64.decode(img_string_data,Base64.DEFAULT);
        //Initialize bitmap
        Bitmap bitmap = BitmapFactory.decodeByteArray(bytes, 0, bytes.length);
        return bitmap;
    }

    public Bitmap decodeBlobType(byte[] bytes_from_database){

        //get bytes from database
        byte[] bytes = bytes_from_database;

        //byte to bitmap
        Bitmap bitmap = BitmapFactory.decodeByteArray(bytes, 0, bytes.length);

        return bitmap;

    }
}